package Basic_Program;
/*					The Do/While Loop
 * 						do {
  							// code block to be executed
							}
						while (condition);
 */
public class Lab021 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		do {
		  System.out.println(i);
		  i++;
		}
		while (i < 5);
	}

}
