package Basic_Program;
// Multiple Parameters
public class Lab026 {
	static void name(String fname,int age) {
		System.out.println(fname+ " is " +age);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			name("shraddha",25);
			name("Priyank",33);
			name("Mahira",5);
	}

}
//shraddha is 25
//Priyank is 33
//Mahira is 5
